        int a = 0;
        int sayfasayisi = 0;


        private void button2_Click(object sender, EventArgs e)
        {
            if (txtSayfaSayisi.Text == "")
            {
                MessageBox.Show("Sayfa Sayısı Girmeniz Gerekli!", "HTML Kitap Oluşturucu", MessageBoxButtons.OK, MessageBoxIcon.Error);
            }
            else
            {
                listBox1.Items.Clear();

                sayfasayisi = Convert.ToInt32(txtSayfaSayisi.Text);
                listBox1.Items.Add(htmlacis);
                listBox1.Items.Add(head);
                listBox1.Items.Add(charset);
                listBox1.Items.Add(aciklama);
                listBox1.Items.Add(keyword);
                listBox1.Items.Add(author);
                listBox1.Items.Add(title);
                listBox1.Items.Add(metatag);
                listBox1.Items.Add(scriptbir);
                listBox1.Items.Add(scriptiki);
                listBox1.Items.Add(headkapat);
                listBox1.Items.Add(body);
                listBox1.Items.Add(kitapbaslat);
                listBox1.Items.Add(kitapalan);
                listBox1.Items.Add(kitap);

                for (int i = 0; i < sayfasayisi; i++)
                {

                    listBox1.Items.Add(divilk + a.ToString() + divson);

                    a++;
                }

                listBox1.Items.Add(divkapat);
                listBox1.Items.Add(script);
                listBox1.Items.Add(function);
                listBox1.Items.Add(functiondevam);
                listBox1.Items.Add(ikincifunction);
                listBox1.Items.Add(komplekapat);


                a = 0;
                sayfasayisi = 0;
            }
        }
        string s1 = "";

        private void btnKopyala_Click(object sender, EventArgs e)
        {
            foreach (object item in listBox1.Items) s1 += item.ToString() + "\r\n";
            Clipboard.SetText(s1);
        }

        private void Form1_Load(object sender, EventArgs e)
        {
            MessageBox.Show("Kaydettiğiniz Klasörün İçerisinde ki Pages Klasörüne Kitap Sayfası Olarak Kullanılacak Jpeg'leri ilk sayfa 0 olacak şekilde isimlendirerek koymalısınız aksi takdirde proje çalışmayacaktır.", "HTML Kitap Oluşturucu", MessageBoxButtons.OK, MessageBoxIcon.Information);
        }

        private void btnKaydet_Click(object sender, EventArgs e)
        {
            foreach (object item in listBox1.Items) s1 += item.ToString() + "\r\n";
            Clipboard.SetText(s1);
            FolderBrowserDialog kayitsec = new FolderBrowserDialog();
            kayitsec.ShowDialog();
            kayitsec.ShowNewFolderButton = true;
            string KlasorYolu;
            KlasorYolu = kayitsec.SelectedPath;
            Directory.CreateDirectory(KlasorYolu + "/HTMLKitap");
            Directory.CreateDirectory(KlasorYolu + "/HTMLKitap/css");
            Directory.CreateDirectory(KlasorYolu + "/HTMLKitap/pages");
            Directory.CreateDirectory(KlasorYolu + "/HTMLKitap/pics");
            Directory.CreateDirectory(KlasorYolu + "/HTMLKitap/js");

            FileStream basic = new FileStream(KlasorYolu + "/HTMLKitap/css/basic.css", FileMode.OpenOrCreate, FileAccess.Write);
            StreamWriter sw = new StreamWriter(basic);
            sw.WriteLine(css1);
            sw.Flush();
            sw.Close();
            basic.Close();

            FileStream jqueryfs = new FileStream(KlasorYolu + "/HTMLKitap/js/jquery.js", FileMode.OpenOrCreate, FileAccess.Write);
            StreamWriter jquerysw = new StreamWriter(jqueryfs);
            jquerysw.WriteLine(jquery);
            jquerysw.Flush();
            jquerysw.Close();
            jqueryfs.Close();

            FileStream modernzrfs = new FileStream(KlasorYolu + "/HTMLKitap/js/modernizr.js", FileMode.OpenOrCreate, FileAccess.Write);
            StreamWriter modernzrsw = new StreamWriter(modernzrfs);
            modernzrsw.WriteLine(modernzr);
            modernzrsw.Flush();
            modernzrsw.Close();
            modernzrfs.Close();

            FileStream turnfs = new FileStream(KlasorYolu + "/HTMLKitap/js/turn.js", FileMode.OpenOrCreate, FileAccess.Write);
            StreamWriter turnsw = new StreamWriter(turnfs);
            turnsw.WriteLine(turnjs);
            turnsw.Flush();
            turnsw.Close();
            turnfs.Close();

            FileStream turnminfs = new FileStream(KlasorYolu + "/HTMLKitap/js/turnmin.js", FileMode.OpenOrCreate, FileAccess.Write);
            StreamWriter turnminsw = new StreamWriter(turnminfs);
            turnminsw.WriteLine(turnminjs);
            turnminsw.Flush();
            turnminsw.Close();
            turnminfs.Close();

            FileStream index = new FileStream(KlasorYolu + "/HTMLKitap/index.html", FileMode.OpenOrCreate, FileAccess.Write);
            StreamWriter indexsw = new StreamWriter(index);
            indexsw.WriteLine(Clipboard.GetText());
            indexsw.Flush();
            indexsw.Close();
            index.Close();
        }

        private void txtSayfaSayisi_KeyPress(object sender, KeyPressEventArgs e)
        {
            e.Handled = !char.IsDigit(e.KeyChar) && !char.IsControl(e.KeyChar);
        }

